DROP DATABASE IF EXISTS gestion_joueurs;

CREATE DATABASE gestion_joueurs DEFAULT CHARACTER SET utf8;
USE gestion_joueurs;

DROP TABLE IF EXISTS Joueur, Profil;

CREATE TABLE Joueur(
	id int primary key auto_increment,
	nom varchar(20),
	prenom varchar(20),
	dateCreation datetime DEFAULT NOW(),
	dateModification datetime DEFAULT NOW(),
	description text,
	profil int
)  ENGINE=InnoDb DEFAULT CHARSET=utf8;

CREATE TABLE Profil(
	id int primary key auto_increment,
	libelle varchar(20)
)  ENGINE=InnoDb DEFAULT CHARSET=utf8;



ALTER TABLE Joueur ADD CONSTRAINT fk_profil_joueur FOREIGN KEY(profil) REFERENCES Profil(id);
ALTER TABLE Profil ADD CONSTRAINT unicite_libelle UNIQUE(libelle);

GRANT ALL PRIVILEGES ON gestion_joueurs.* TO ges_joueurs_user IDENTIFIED BY 'passer';